import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from '../services/auth.service';
import RecipeService from '../services/recipe.service';
import '../styles/RecipeForm.css';

function RecipeFormPage() {
  const navigate = useNavigate();
  const [showUrlModal, setShowUrlModal] = useState(false);
  const [isParsing, setIsParsing] = useState(false);
  const [loading, setLoading] = useState(false);
  
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    cuisine: '',
    difficulty: 'Ïâ¨ÏõÄ',
    cookingTime: '',
    servings: '',
    tags: []
  });

  const [ingredients, setIngredients] = useState([]);
  const [newIngredient, setNewIngredient] = useState({ name: '', amount: '' });
  
  const [steps, setSteps] = useState(['']);

  // Î°úÍ∑∏Ïù∏ ÌôïÏù∏
  React.useEffect(() => {
    if (!AuthService.isAuthenticated()) {
      navigate('/login');
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleAddIngredient = () => {
    if (newIngredient.name && newIngredient.amount) {
      setIngredients([...ingredients, newIngredient]);
      setNewIngredient({ name: '', amount: '' });
    }
  };

  const handleRemoveIngredient = (index) => {
    setIngredients(ingredients.filter((_, i) => i !== index));
  };

  const handleStepChange = (index, value) => {
    const newSteps = [...steps];
    newSteps[index] = value;
    setSteps(newSteps);
  };

  const handleAddStep = () => {
    setSteps([...steps, '']);
  };

  const handleRemoveStep = (index) => {
    if (steps.length > 1) {
      setSteps(steps.filter((_, i) => i !== index));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨
    if (ingredients.length === 0) {
      alert('Ïû¨Î£åÎ•º ÏµúÏÜå 1Í∞ú Ïù¥ÏÉÅ Ï∂îÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    if (steps.filter(s => s.trim()).length === 0) {
      alert('Ï°∞Î¶¨ ÏàúÏÑúÎ•º ÏµúÏÜå 1Í∞ú Ïù¥ÏÉÅ Ï∂îÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);

    try {
      const recipeData = {
        ...formData,
        cookingTime: parseInt(formData.cookingTime),
        servings: parseInt(formData.servings) || 1,
        ingredients,
        steps: steps.filter(s => s.trim()),
        tags: formData.tags ? formData.tags.split(',').map(t => t.trim()) : []
      };

      const response = await RecipeService.createRecipe(recipeData);

      if (response.success) {
        alert('Î†àÏãúÌîºÍ∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Î†àÏãúÌîº Ï†ÄÏû• Ïã§Ìå®:', error);
      alert(error.error?.message || 'Î†àÏãúÌîº Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const handleUrlParsing = () => {
    setShowUrlModal(false);
    setIsParsing(true);
    
    setTimeout(() => {
      setIsParsing(false);
      alert('URL ÌååÏã± Í∏∞Îä•ÏùÄ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.');
    }, 2000);
  };

  const handleLogout = () => {
    if (window.confirm('Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      AuthService.logout();
    }
  };

  return (
    <div className="recipe-form-container">
      {/* Ìó§Îçî */}
      <header className="dashboard-header">
        <div className="logo">üç≥ RecipeNote</div>
        <nav className="nav">
          <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
          <a href="/profile">ÌîÑÎ°úÌïÑ</a>
          <a onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</a>
        </nav>
      </header>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="recipe-form-content">
        <div className="recipe-form">
          <div className="page-header">
            <h1 className="page-title">ÏÉà Î†àÏãúÌîº Ï∂îÍ∞Ä</h1>
            <p className="page-subtitle">URLÎ°ú ÏûêÎèô Ï±ÑÏö∞Í±∞ÎÇò ÏßÅÏ†ë ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>
          </div>

          {/* URL ÏûêÎèô Ï±ÑÏö∞Í∏∞ ÏÑπÏÖò */}
          <div className="url-section">
            <h3>üîó URLÎ°ú ÏûêÎèô Ï±ÑÏö∞Í∏∞</h3>
            <p>YouTube, Î∏îÎ°úÍ∑∏ Î†àÏãúÌîº URLÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä ÏûêÎèôÏúºÎ°ú Ï±ÑÏõåÎìúÎ†§Ïöî!</p>
            <button className="btn-primary" onClick={() => setShowUrlModal(true)}>
              URL ÏûÖÎ†•ÌïòÍ∏∞
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Ï†úÎ™© *</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Î†àÏãúÌîº Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                required
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>ÏÑ§Î™Ö</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                placeholder="Í∞ÑÎã®Ìïú ÏÑ§Î™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                rows="3"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label>Ïû¨Î£å *</label>
              {ingredients.map((ingredient, index) => (
                <div key={index} className="ingredient-list-item">
                  <span>{ingredient.name} - {ingredient.amount}</span>
                  <button 
                    type="button" 
                    className="btn-delete"
                    onClick={() => handleRemoveIngredient(index)}
                    disabled={loading}
                  >
                    ÏÇ≠Ï†ú
                  </button>
                </div>
              ))}
              <div className="ingredient-input-group">
                <input 
                  type="text" 
                  placeholder="Ïû¨Î£åÎ™Ö"
                  value={newIngredient.name}
                  onChange={(e) => setNewIngredient({ ...newIngredient, name: e.target.value })}
                  disabled={loading}
                />
                <input 
                  type="text" 
                  placeholder="Ïñë"
                  value={newIngredient.amount}
                  onChange={(e) => setNewIngredient({ ...newIngredient, amount: e.target.value })}
                  disabled={loading}
                />
                <button 
                  type="button" 
                  className="btn-add"
                  onClick={handleAddIngredient}
                  disabled={loading}
                >
                  Ï∂îÍ∞Ä
                </button>
              </div>
            </div>

            <div className="form-group">
              <label>Ï°∞Î¶¨ ÏàúÏÑú *</label>
              {steps.map((step, index) => (
                <div key={index} style={{ marginBottom: '12px' }}>
                  <div style={{ display: 'flex', gap: '12px', alignItems: 'flex-start' }}>
                    <div className="step-number">{index + 1}</div>
                    <textarea
                      className="step-textarea"
                      placeholder={`${index + 1}Î≤àÏß∏ Îã®Í≥ÑÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî`}
                      value={step}
                      onChange={(e) => handleStepChange(index, e.target.value)}
                      disabled={loading}
                    />
                    {steps.length > 1 && (
                      <button
                        type="button"
                        className="btn-delete"
                        onClick={() => handleRemoveStep(index)}
                        disabled={loading}
                        style={{ marginTop: '10px' }}
                      >
                        ÏÇ≠Ï†ú
                      </button>
                    )}
                  </div>
                </div>
              ))}
              <button 
                type="button" 
                className="btn-outline" 
                style={{ width: '100%' }}
                onClick={handleAddStep}
                disabled={loading}
              >
                + Îã®Í≥Ñ Ï∂îÍ∞Ä
              </button>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Ïπ¥ÌÖåÍ≥†Î¶¨ *</label>
                <select 
                  name="category" 
                  value={formData.category} 
                  onChange={handleChange} 
                  required
                  disabled={loading}
                >
                  <option value="">ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
                  <option value="Î©îÏù∏ ÏöîÎ¶¨">Î©îÏù∏ ÏöîÎ¶¨</option>
                  <option value="Î∞òÏ∞¨">Î∞òÏ∞¨</option>
                  <option value="Íµ≠/Ï∞åÍ∞ú">Íµ≠/Ï∞åÍ∞ú</option>
                  <option value="ÎîîÏ†ÄÌä∏">ÎîîÏ†ÄÌä∏</option>
                </select>
              </div>

              <div className="form-group">
                <label>ÎÇúÏù¥ÎèÑ *</label>
                <select 
                  name="difficulty" 
                  value={formData.difficulty} 
                  onChange={handleChange}
                  disabled={loading}
                >
                  <option value="Ïâ¨ÏõÄ">Ïâ¨ÏõÄ</option>
                  <option value="Î≥¥ÌÜµ">Î≥¥ÌÜµ</option>
                  <option value="Ïñ¥Î†§ÏõÄ">Ïñ¥Î†§ÏõÄ</option>
                </select>
              </div>

              <div className="form-group">
                <label>Ï°∞Î¶¨ ÏãúÍ∞Ñ (Î∂Ñ) *</label>
                <input
                  type="number"
                  name="cookingTime"
                  value={formData.cookingTime}
                  onChange={handleChange}
                  placeholder="30"
                  required
                  disabled={loading}
                />
              </div>
            </div>

            <div className="form-group">
              <label>ÌÉúÍ∑∏</label>
              <input
                type="text"
                name="tags"
                value={formData.tags}
                onChange={handleChange}
                placeholder="ÌÉúÍ∑∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî (ÏâºÌëúÎ°ú Íµ¨Î∂Ñ)"
                disabled={loading}
              />
            </div>

            <div className="action-buttons">
              <button 
                type="submit" 
                className="btn-primary"
                disabled={loading}
              >
                {loading ? 'Ï†ÄÏû• Ï§ë...' : 'Ï†ÄÏû•ÌïòÍ∏∞'}
              </button>
              <button 
                type="button" 
                className="btn-secondary" 
                onClick={() => navigate('/dashboard')}
                disabled={loading}
              >
                Ï∑®ÏÜå
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* URL ÏûÖÎ†• Î™®Îã¨ */}
      {showUrlModal && (
        <div className="modal" onClick={() => setShowUrlModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">üîó URL ÏûÖÎ†•</h2>
            <p className="modal-subtitle">YouTube, Î∏îÎ°úÍ∑∏ Î†àÏãúÌîº URLÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>
            
            <div className="form-group">
              <input type="text" placeholder="https://youtube.com/watch?v=..." />
            </div>
            
            <div className="modal-buttons">
              <button className="btn-primary" onClick={handleUrlParsing}>
                ÌååÏã± ÏãúÏûë
              </button>
              <button className="btn-secondary" onClick={() => setShowUrlModal(false)}>
                Ï∑®ÏÜå
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ÌååÏã± Ï§ë Î™®Îã¨ */}
      {isParsing && (
        <div className="modal">
          <div className="modal-content">
            <div className="loading">
              <div className="spinner"></div>
              <h3>AIÍ∞Ä Î†àÏãúÌîºÎ•º Î∂ÑÏÑùÌïòÍ≥† ÏûàÏäµÎãàÎã§...</h3>
              <p>ÏïΩ 30Ï¥à ÏÜåÏöîÎê©ÎãàÎã§ ‚òï</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default RecipeFormPage;
