import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import AuthService from '../services/auth.service';
import RecipeService from '../services/recipe.service';
import '../styles/RecipeDetail.css';

function RecipeDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Ïù∏Ï¶ù ÌôïÏù∏
    if (!AuthService.isAuthenticated()) {
      navigate('/login');
      return;
    }

    loadRecipe();
  }, [id, navigate]);

  const loadRecipe = async () => {
    try {
      setLoading(true);
      const response = await RecipeService.getRecipe(id);
      
      if (response.success) {
        setRecipe(response.data);
      }
    } catch (error) {
      console.error('Î†àÏãúÌîº Î°úÎìú Ïã§Ìå®:', error);
      alert('Î†àÏãúÌîºÎ•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Ï†ïÎßê Ïù¥ Î†àÏãúÌîºÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      return;
    }

    try {
      const response = await RecipeService.deleteRecipe(id);
      
      if (response.success) {
        alert('Î†àÏãúÌîºÍ∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Î†àÏãúÌîº ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      alert('Î†àÏãúÌîº ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  const handleLogout = () => {
    if (window.confirm('Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      AuthService.logout();
    }
  };

  if (loading) {
    return (
      <div className="recipe-detail-container">
        <header className="dashboard-header">
          <div className="logo">üç≥ RecipeNote</div>
          <nav className="nav">
            <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
            <a href="/profile">ÌîÑÎ°úÌïÑ</a>
            <a onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</a>
          </nav>
        </header>
        <div style={{ textAlign: 'center', padding: '100px 20px' }}>
          <div className="spinner"></div>
          <p style={{ marginTop: '20px', color: '#718096' }}>Î†àÏãúÌîºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
        </div>
      </div>
    );
  }

  if (!recipe) {
    return (
      <div className="recipe-detail-container">
        <header className="dashboard-header">
          <div className="logo">üç≥ RecipeNote</div>
          <nav className="nav">
            <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
            <a href="/profile">ÌîÑÎ°úÌïÑ</a>
            <a onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</a>
          </nav>
        </header>
        <div style={{ textAlign: 'center', padding: '100px 20px' }}>
          <h2>Î†àÏãúÌîºÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§</h2>
          <button className="btn-primary" onClick={() => navigate('/dashboard')}>
            ÎèåÏïÑÍ∞ÄÍ∏∞
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="recipe-detail-container">
      {/* Ìó§Îçî */}
      <header className="dashboard-header">
        <div className="logo">üç≥ RecipeNote</div>
        <nav className="nav">
          <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
          <a href="/profile">ÌîÑÎ°úÌïÑ</a>
          <a onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</a>
        </nav>
      </header>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="recipe-detail-content">
        <button className="btn-back" onClick={() => navigate('/dashboard')}>
          ‚Üê ÎèåÏïÑÍ∞ÄÍ∏∞
        </button>

        <div className="detail-header">
          <h1 className="detail-title">{recipe.title}</h1>
          <div className="recipe-meta">
            <span>‚è±Ô∏è {recipe.cookingTime}Î∂Ñ</span>
            <span>üë§ {recipe.difficulty}</span>
            <span>üçΩÔ∏è {recipe.servings || 2}Ïù∏Î∂Ñ</span>
          </div>
          {recipe.tags && recipe.tags.length > 0 && (
            <div className="tags">
              {recipe.tags.map((tag) => (
                <span key={tag} className="tag">{tag}</span>
              ))}
            </div>
          )}
        </div>

        <div className="detail-image">{recipe.emoji || 'üçΩÔ∏è'}</div>

        {recipe.description && (
          <div style={{ 
            padding: '20px', 
            background: '#F7F9FC', 
            borderRadius: '12px', 
            marginBottom: '40px',
            color: '#2D3748',
            lineHeight: '1.6'
          }}>
            {recipe.description}
          </div>
        )}

        <h2 className="section-title">üìù Ïû¨Î£å</h2>
        <div className="ingredients-list">
          {recipe.ingredients && recipe.ingredients.map((ingredient, index) => (
            <div key={index} className="ingredient-item">
              <span>{ingredient.name}</span>
              <span>{ingredient.amount}</span>
            </div>
          ))}
        </div>

        <h2 className="section-title">üë®‚Äçüç≥ Ï°∞Î¶¨ ÏàúÏÑú</h2>
        <div className="steps-list">
          {recipe.steps && recipe.steps.map((step, index) => (
            <div key={index} className="step-item">
              <div className="step-number">{index + 1}</div>
              <div>{step}</div>
            </div>
          ))}
        </div>

        <div className="action-buttons">
          <button 
            className="btn-primary" 
            onClick={() => alert('ÏàòÏ†ï Í∏∞Îä•ÏùÄ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.')}
          >
            ÏàòÏ†ïÌïòÍ∏∞
          </button>
          <button 
            className="btn-secondary"
            onClick={() => alert('Î≥¥Í¥ÄÌï® Í∏∞Îä•ÏùÄ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.')}
          >
            Î≥¥Í¥ÄÌï®ÏúºÎ°ú
          </button>
          <button 
