import React, { useState } from 'react';
import '../styles/RecipeForm.css';

function RecipeFormPage() {
  const [showUrlModal, setShowUrlModal] = useState(false);
  const [isParsing, setIsParsing] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    cuisine: '',
    difficulty: 'Ïâ¨ÏõÄ',
    cookingTime: '',
    servings: ''
  });

  const [ingredients, setIngredients] = useState([
    { name: 'ÍπÄÏπò', amount: '1Ïªµ' },
    { name: 'ÎèºÏßÄÍ≥†Í∏∞', amount: '200g' }
  ]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Î†àÏãúÌîº Ï†ÄÏû•:', formData, ingredients);
    // TODO: API Ìò∏Ï∂ú
    alert('Î†àÏãúÌîºÍ∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
    window.location.href = '/dashboard';
  };

  const handleUrlParsing = () => {
    setShowUrlModal(false);
    setIsParsing(true);
    
    // 3Ï¥à ÌõÑ ÌååÏã± ÏôÑÎ£å ÏãúÎÆ¨Î†àÏù¥ÏÖò
    setTimeout(() => {
      setIsParsing(false);
      alert('‚úÖ ÌååÏã± ÏôÑÎ£å!\n\nÎ†àÏãúÌîºÍ∞Ä ÏûêÎèôÏúºÎ°ú ÏûÖÎ†•ÎêòÏóàÏäµÎãàÎã§.');
    }, 3000);
  };

  return (
    <div className="recipe-form-container">
      {/* Ìó§Îçî */}
      <header className="dashboard-header">
        <div className="logo">üç≥ RecipeNote</div>
        <nav className="nav">
          <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
          <a href="/profile">ÌîÑÎ°úÌïÑ</a>
          <a href="/login">Î°úÍ∑∏ÏïÑÏõÉ</a>
        </nav>
      </header>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="recipe-form-content">
        <div className="recipe-form">
          <div className="page-header">
            <h1 className="page-title">ÏÉà Î†àÏãúÌîº Ï∂îÍ∞Ä</h1>
            <p className="page-subtitle">URLÎ°ú ÏûêÎèô Ï±ÑÏö∞Í±∞ÎÇò ÏßÅÏ†ë ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>
          </div>

          {/* URL ÏûêÎèô Ï±ÑÏö∞Í∏∞ ÏÑπÏÖò */}
          <div className="url-section">
            <h3>üîó URLÎ°ú ÏûêÎèô Ï±ÑÏö∞Í∏∞</h3>
            <p>YouTube, Î∏îÎ°úÍ∑∏ Î†àÏãúÌîº URLÏùÑ ÏûÖÎ†•ÌïòÎ©¥ AIÍ∞Ä ÏûêÎèôÏúºÎ°ú Ï±ÑÏõåÎìúÎ†§Ïöî!</p>
            <button className="btn-primary" onClick={() => setShowUrlModal(true)}>
              URL ÏûÖÎ†•ÌïòÍ∏∞
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Ï†úÎ™© *</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                placeholder="Î†àÏãúÌîº Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                required
              />
            </div>

            <div className="form-group">
              <label>ÏÑ§Î™Ö</label>
              <textarea
                name="description"
