import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from '../services/auth.service';
import '../styles/Auth.css';

function LoginPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError(''); // ÏûÖÎ†• Ïãú ÏóêÎü¨ Ï¥àÍ∏∞Ìôî
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await AuthService.login(formData);
      // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ - ÎåÄÏãúÎ≥¥ÎìúÎ°ú Ïù¥Îèô
      navigate('/dashboard');
    } catch (err) {
      setError(err.error?.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = () => {
    alert('Google Î°úÍ∑∏Ïù∏ÏùÄ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.');
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1 className="auth-title">RecipeNote</h1>
        <p className="auth-subtitle">ÎÇòÎßåÏùò Î†àÏãúÌîºÎ•º Ìïú Í≥≥ÏóêÏÑú Í¥ÄÎ¶¨ÌïòÏÑ∏Ïöî</p>

        <button className="btn-google" onClick={handleGoogleLogin}>
          <span className="google-icon">üîµ</span>
          GoogleÎ°ú ÏãúÏûëÌïòÍ∏∞
        </button>

        <div className="divider">ÎòêÎäî</div>

        {error && (
          <div style={{
            padding: '12px',
            background: '#FEE',
            color: '#C53030',
            borderRadius: '8px',
            marginBottom: '20px',
            fontSize: '14px',
            fontWeight: '500'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>ÏïÑÏù¥Îîî</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="ÏïÑÏù¥ÎîîÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label>ÎπÑÎ∞ÄÎ≤àÌò∏</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              required
              disabled={loading}
            />
          </div>

          <button 
            type="submit" 
            className="btn-primary"
            disabled={loading}
          >
            {loading ? 'Î°úÍ∑∏Ïù∏ Ï§ë...' : 'Î°úÍ∑∏Ïù∏'}
          </button>

          <button
            type="button"
            className="btn-secondary"
            onClick={() => navigate('/register')}
            disabled={loading}
          >
            ÌöåÏõêÍ∞ÄÏûÖ
          </button>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
