import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from '../services/auth.service';
import RecipeService from '../services/recipe.service';
import '../styles/Dashboard.css';

function DashboardPage() {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState('Ï†ÑÏ≤¥');
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Ïù∏Ï¶ù ÌôïÏù∏
    if (!AuthService.isAuthenticated()) {
      navigate('/login');
      return;
    }

    // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const userData = AuthService.getUser();
    setUser(userData);

    // Î†àÏãúÌîº Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    loadRecipes();
  }, [navigate]);

  const loadRecipes = async () => {
    try {
      setLoading(true);
      const response = await RecipeService.getRecipes();
      
      if (response.success) {
        setRecipes(response.data.recipes || []);
      }
    } catch (error) {
      console.error('Î†àÏãúÌîº Î°úÎìú Ïã§Ìå®:', error);
      alert('Î†àÏãúÌîºÎ•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    if (window.confirm('Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      AuthService.logout();
    }
  };

  const handleRecipeClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`);
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      loadRecipes();
      return;
    }

    try {
      setLoading(true);
      const response = await RecipeService.searchRecipes(searchQuery);
      
      if (response.success) {
        setRecipes(response.data || []);
      }
    } catch (error) {
      console.error('Í≤ÄÏÉâ Ïã§Ìå®:', error);
      alert('Í≤ÄÏÉâÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  const filters = ['Ï†ÑÏ≤¥', 'üçΩÔ∏è Î©îÏù∏ ÏöîÎ¶¨', 'ü•ó Î∞òÏ∞¨', 'üçú Íµ≠/Ï∞åÍ∞ú', 'üç∞ ÎîîÏ†ÄÌä∏', '‚ö° 30Î∂Ñ Ïù¥ÎÇ¥', 'üòä Ïâ¨ÏõÄ'];

  return (
    <div className="dashboard-container">
      {/* Ìó§Îçî */}
      <header className="dashboard-header">
        <div className="logo">üç≥ RecipeNote</div>
        <nav className="nav">
          <a href="/dashboard">ÎÇ¥ Î†àÏãúÌîº</a>
          <a href="/profile">ÌîÑÎ°úÌïÑ</a>
          <a onClick={handleLogout} style={{ cursor: 'pointer' }}>Î°úÍ∑∏ÏïÑÏõÉ</a>
        </nav>
      </header>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="dashboard-content">
        <div className="page-header">
          <h1 className="page-title">ÎÇ¥ Î†àÏãúÌîº</h1>
          <p className="page-subtitle">
            {user ? `ÏïàÎÖïÌïòÏÑ∏Ïöî, ${user.name}Îãò! ` : ''}
            {recipes.length}Í∞úÏùò Î†àÏãúÌîºÎ•º Ï†ÄÏû•ÌñàÏñ¥Ïöî üéâ
          </p>
        </div>

        {/* ÌÜµÍ≥Ñ Ïπ¥Îìú */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-value">{recipes.length}</div>
            <div className="stat-label">Ï†ÑÏ≤¥ Î†àÏãúÌîº</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">0</div>
            <div className="stat-label">Ï¶êÍ≤®Ï∞æÍ∏∞</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">0</div>
            <div className="stat-label">Ïù¥Î≤à Ï£º Ï∂îÍ∞Ä</div>
          </div>
        </div>

        {/* Í≤ÄÏÉâ Î∞î */}
        <div className="search-bar">
          <input
            type="text"
            className="search-input"
            placeholder="üîç Î†àÏãúÌîº, Ïû¨Î£å Í≤ÄÏÉâ..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          />
          <button className="btn-search" onClick={handleSearch}>Í≤ÄÏÉâ</button>
        </div>

        {/* ÌïÑÌÑ∞ */}
        <div className="filters">
          {filters.map((filter) => (
            <button
              key={filter}
              className={`filter-btn ${activeFilter === filter ? 'active' : ''}`}
              onClick={() => setActiveFilter(filter)}
            >
              {filter}
            </button>
          ))}
        </div>

        {/* Î†àÏãúÌîº Í∑∏Î¶¨Îìú */}
        {loading ? (
          <div style={{ textAlign: 'center', padding: '40px' }}>
            <div className="spinner"></div>
            <p style={{ marginTop: '20px', color: '#718096' }}>Î†àÏãúÌîºÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
          </div>
        ) : recipes.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '80px 20px',
            color: '#718096'
          }}>
            <div style={{ fontSize: '64px', marginBottom: '20px' }}>üìù</div>
            <h3 style={{ fontSize: '24px', marginBottom: '12px' }}>ÏïÑÏßÅ Î†àÏãúÌîºÍ∞Ä ÏóÜÏäµÎãàÎã§</h3>
            <p>Ïö∞Ï∏° ÌïòÎã®Ïùò + Î≤ÑÌäºÏùÑ ÎàåÎü¨ Ï≤´ Î†àÏãúÌîºÎ•º Ï∂îÍ∞ÄÌï¥Î≥¥ÏÑ∏Ïöî!</p>
          </div>
        ) : (
          <div className="recipe-grid">
            {recipes.map((recipe, index) => (
              <div 
                key={recipe.recipeId} 
                className="recipe-card" 
                onClick={() => handleRecipeClick(recipe.recipeId)}
              >
                <div className="recipe-image" style={{
                  background: index % 4 === 0 ? 'linear-gradient(135deg, #FFE66D 0%, #FFD93D 100%)' :
                             index % 4 === 1 ? 'linear-gradient(135deg, #FF9A9E 0%, #FAD0C4 100%)' :
                             index % 4 === 2 ? 'linear-gradient(135deg, #A8EDEA 0%, #FED6E3 100%)' :
